't exist    $sql = "CREATE TABLE IF NOT EXISTS passwords (        id INT(11) UNSIGNED AUTO_INCREMENT PRIMARY KEY,        user_id INT(11) UNSIGNED NOT NULL,        password VARCHAR(255) NOT NULL,        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP    )";    $db->exec($sql);    // Close the database connection    $db = null;} catch (PDOException $e) {    // Handle any exceptions or errors    die("Error creating password table: " . $e->getMessage());}